;(function(){tinymce.create('tinymce.plugins.LioniteImages',{init:function(ed,url){ed.addCommand('mceLioniteImages',function(){ed.windowManager.open({file:url+'/lioniteimages.htm',width:700+parseInt(ed.getLang('lioniteImages.delta_width',0)),height:507+parseInt(ed.getLang('lioniteImages.delta_height',0)),inline:true,popup_css:false},{plugin_url:url})});ed.addButton('lioniteimages',{title:'Lionite Image Manager',cmd:'mceLioniteImages',image:url+'/img/icon.png'})},getInfo:function(){return{longname:'Lionite Image Manager',author:'Eran Galperin / Lionite',authorurl:'http://www.lionite.com',infourl:'http://demo.lionite.com/imagemanager',version:'1.0'}}});tinymce.PluginManager.add('lioniteimages',tinymce.plugins.LioniteImages)})();